mapper.update:
UPDATE inquiries
SET progress = '접수 완료'
WHERE inquiry_id = #{inquiryId}

UPDATE inquiries
SET progress = '답변 완료', answer = #{answer}, answer_date = NOW()
WHERE inquiry_id = #{inquiryId}

mapper.select:
SELECT image image, sticker_image stickerImage
FROM challenges
WHERE challenge_id = #{challengeId}
